import React, { Component } from 'react'

export class NewsItem extends Component {
    
  render() {
  let  {title,description,imageUrl,url,author,date,source}=this.props;
    return (
      <div className="my-3">
       <div className="card" style={{width: "18rem"}}>
  <img src={imageUrl?imageUrl:"https://static.theprint.in/wp-content/uploads/2022/10/ESO-VLT.jpg"} className="card-img-top" alt="..."/>
  <div className="card-body">
  <span class="position-absolute top-0  translate-middle badge rounded-pill bg-danger" style={{left:'90%', zIndex:'1'}}>
    {source}
  </span>
    <h5 className="card-title">{title}...</h5>
    <p className="card-text">{description}...</p>
    <p class="card-text"><small class="text-muted">By {author} on {new Date(date).toGMTString()}</small></p>
    <a href={url} target="_blank" rel="noreferrer" className="btn btn-sm btn-dark">Read More</a>
  </div>
</div>
      </div>
    )
  }
}

export default NewsItem
